import { IsNotEmpty, IsEmail } from 'class-validator';

/**
 * Data transfer object (DTO) with expected fields for creating a user.
 * `id` and `workspaces` are excluded since the former is generated by database,
 * and the latter is managed by the workspaces service.
 */
export class CreateUserDto {
  @IsNotEmpty()
  firstName: string;

  @IsNotEmpty()
  lastName: string;

  @IsEmail()
  email: string;
}
